import React, { useContext, useEffect } from 'react';
import './App.css';
import PlanetsContext from './PlanetsContext';

function App() {
  const { fetchData } = useContext(PlanetsContext);

  useEffect(() => {
    const getData = async() =>{
      fetchData();
    };
    getData();
  });


  return (
    <span>Hello, there general kenobi!</span>
  );
}

export default App;
