import React, { useContext, useEffect } from 'react';
import './App.css';
import PlanetsContext from './PlanetsContext';
import Table from './Table';

function App() {
  const { fetchData, setLoading } = useContext(PlanetsContext);

  useEffect(() => {
    const getData = async() =>{
      fetchData();
    };
    getData();
    setLoading(false);
  },[]);


  return (
    <Table />
  );
}

export default App;
